select T1.* from amall_goods T1 left join amall_goods_combin T2 on T1.id=T2.amall_goods_id
left join amall_goods T3 on T2.combin_goods_id=T3.id
 where T2.combin_goods_id=#{goodsId}

  	select a.id as classId,b.id as goodsId,b.goods_price as price,b.goods_name as goodsName,b.goods_store_id as storeId,d.path as path,d.name as name  from amall_goodsclass as a,amall_goods as b,amall_goodsmodule as c ,amall_accessory as d
	where a.id=b.gc_id and b.module_id=c.id and b.goods_main_photo_id=d.id and c.id=99   
	and a.id = #{id,jdbcType=BIGINT}
